const centerReducer = (state = {}, action) => {
  switch (action.type) {
    case 'SET_CENTER':
      return action.data
    default:
      return state
  }
}

export const setMyStop = position => {
  return dispatch => {
    dispatch({
      type: 'SET_CENTER',
      data: position,
    })
  }
}

export default centerReducer
