import React from 'react' 


const SelectTram = ({ trams, setShowTrams }) => {


  const handleChooseTram = e => {
    console.log('TRAM CHOSEN: ', e.target.value) 
    console.log('TRAMS:', trams.find(tram => tram.VP.veh === e.target.value))
    let chosenTram = trams.map(tram => tram.VP.veh === e.target.value)
    console.log('chosen Tram:', chosenTram)
    //setShowTrams(chosenTram)
  }



  return ( 
    <div>
      <p>valitse ratikka:</p>
      <select onChange={handleChooseTram}>
        {trams.map((tram, i) => (
          <option key={i} value={tram.VP.veh}>
            linja: {tram.VP.desi} veh: {tram.VP.veh}
          </option>
        ))}
      </select>
    </div>
  )
}

export default SelectTram
