import React from 'react'
import { connect } from 'react-redux'
import { setShowTrams } from '../reducers/showTramsReducer'
import { setMyStop } from '../reducers/myStopReducer'
import { setCenter } from '../reducers/centerReducer'
import { setZoom } from '../reducers/zoomReducer'
import { setMyTram } from '../reducers/myTramReducer'
import { Container, Row, Col, Form, Button } from 'react-bootstrap'
import distance from '../utils/helpers'

const Sidebar = ({
  closeSidebar,
  showSidebar,
  trams,
  //showTrams,
  setShowTrams,
  setCenter,
  setZoom,
  stops,
  myStop,
  setMyStop,
  myTram,
  setMyTram
}) => {
  const style = showSidebar ? { width: '250px' } : { width: '0' }

  const handleChooseMyTram = e => {
    console.log('TRAM CHOSEN: ', e.target.value)
    if (e.target.value !== 'reset') {
      let chosenTram = trams.find(
        tram => tram.VP.veh.toString() === e.target.value
      )
      console.log('chosen Tram:', chosenTram) 
      setMyTram(chosenTram)
      setShowTrams([])
      setCenter({ lat: chosenTram.VP.lat, lng: chosenTram.VP.long })
      setZoom(16)
    } else {
      setMyTram('')
    }
  }

  const handleShowLine = e => {
    console.log('LINE CHOSEN: ', e.target.value)
    if (e.target.value !== '-') {
      setShowTrams(
        trams.filter(tram => parseInt(tram.VP.desi) == e.target.value)
      )
      setZoom(13)
    }
  }

  const handleChooseStop = e => {
    console.log('STOP CHOSEN: ', e.target.value)
    if (e.target.value !== '-') {
      setMyStop(stops.find(stop => stop.gtfsId === e.target.value))
    } else {
      setMyStop('')
    }
  }

  /// COMPARE FUNCTION FOR ARRAY SORT()

  let tramsInOrder = [...trams]
  tramsInOrder.sort(sortByLineNumbers)

  const sortLineNumbers = (a, b) => {
    return parseInt(a) < parseInt(b) ? -1 : parseInt(a) > parseInt(b) ? 1 : 0
  }
  const lineNumbers = []
  trams.forEach(tram => {
    if (!lineNumbers.includes(parseInt(tram.VP.desi))) {
      lineNumbers.push(parseInt(tram.VP.desi))
    }
  })
  lineNumbers.sort(sortLineNumbers)

  const sortStopNames = (a, b) => {
    return a.name < b.name ? -1 : a.name > b.name ? 1 : 0
  }

  const stopsInOrder = [...stops]
  stopsInOrder.sort(sortStopNames)

  return (
    <div style={style} className='sidebar' id='mySidebar'>
      {/* <a
        href='javascript:void(0)'
        className='closebtn'
        onClick={() => closeSidebar()}
      >
        â˜°
      </a> */}
      <Container>
        <Form>



          <Row>
            <Col xs="auto">
              <Form.Group controlId='exampleForm.ControlSelect3'>
                <Form.Label>My stop</Form.Label>
                <Form.Control
                  as='select'
                  onChange={handleChooseStop}
                  value={myStop.gtfsId}
                >
                  <option> - </option>
                  {stopsInOrder.map((stop, i) => (
                    <option key={i} value={stop.gtfsId}>
                      {stop.name} {stop.gtfsId}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
         {/*  {myStop && <Row>
            <Col xs="auto">
              <Form.Group controlId='exampleForm.ControlSelect4'>
                <Form.Label>
                  My stop :  {myStop.name}  {myStop.gtfsId}
                </Form.Label>
              </Form.Group>
            </Col>
          </Row>} */}
          <Row>
            {(myTram.VP && myStop) && (
              <Col xs="auto">
                distance from myTram to myStop:{' '}
                {distance(
                  myStop.lat,
                  myStop.lon,
                  trams.find(tram => tram.VP.veh === myTram.VP.veh).VP
                    .lat,
                  trams.find(tram => tram.VP.veh === myTram.VP.veh).VP
                    .long
                ).toFixed(2)} km
              </Col>
            )}
          </Row>
          <Row>
            <Col >
              <Form.Group controlId='exampleForm.ControlSelect1'>
                <Form.Label>Choose tram</Form.Label>
                <Form.Control
                 as='select' 
                 onChange={handleChooseMyTram}  
                 >
                  {!myTram.VP ? <option> - </option> : <option value="reset">reset</option>} 
                  {tramsInOrder.map((tram, i) => (
                    <option key={i} value={tram.VP.veh}>
                      line: {tram.VP.desi} veh:{tram.VP.veh}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>

         <Row>
            <Col>
              <Button
                onClick={() => {
                  setShowTrams(trams)
                  setZoom(13)
                  closeSidebar()
                }}
                variant='outline-secondary'
              >
                show all trams
              </Button>
            </Col>
          </Row>  

            <Row>
            <Col>
              <Button
                onClick={() => setShowTrams([])}
                variant='outline-secondary'
              >
                hide all trams
              </Button>
            </Col>
          </Row> 

            <Row>
            <Col >
              <Form.Group controlId='exampleForm.ControlSelect2'>
                <Form.Label>Show line</Form.Label>
                <Form.Control as='select' onChange={handleShowLine}>
                  <option> - </option>
                  {lineNumbers.map((line, i) => (
                    <option key={i} value={line}>
                      {line}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>  

          {/*  <Row>
            <Col sm={5}>
              <Form.Group>
                <Form.Check
                  type='radio'
                  label='All times'
                  name='formHorizontalRadios'
                  id='formHorizontalRadios1'
                />
                <Form.Check
                  type='radio'
                  label='today'
                  name='formHorizontalRadios'
                  id='formHorizontalRadios2'
                />
                <Form.Check
                  type='radio'
                  label='this week'
                  name='formHorizontalRadios'
                  id='formHorizontalRadios3'
                />
              </Form.Group>
            </Col>   
          </Row> */}
        </Form>
      </Container>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    trams: state.trams,
    //showTrams: state.showTrams,
    showSidebar: state.showSidebar,
    stops: state.stops,
    myStop: state.myStop,
    myTram: state.myTram,
  }
}

const mapDispatchToProps = {
  setShowTrams,
  setMyStop,
  setCenter,
  setZoom,
  setMyTram,
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Sidebar)
