import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import { setTrams } from './reducers/tramsReducer'
import { setShowSidebar } from './reducers/showSidebarReducer'
import { setShowSidebarOpenButton } from './reducers/showSidebarOpenButtonReducer'
import { setStops } from './reducers/stopsReducer'
import { setMyStop } from './reducers/myStopReducer'
import { setShowTrams } from './reducers/showTramsReducer'
import './App.css'
import LeafletMap from './components/LeafletMap'
import Sidebar from './components/Sidebar'
import { Alert, Button } from 'react-bootstrap'
import  client , { query } from '.'

/* const client = new ApolloClient({
  uri: 'https://api.digitransit.fi/routing/v1/routers/hsl/index/graphql',
})

const query = gql`
  {
    stopsByRadius(lat: 60.170627, lon: 24.939946, radius: 300) {
      edges {
        node {
          stop {
            id
            gtfsId
            name
            lat
            lon
            vehicleType
          }
        }
      }
    }
  }
` */

const App = ({
  setTrams,
  setShowTrams,
  setShowSidebar,
  setShowSidebarOpenButton,
  setStops,
  setMyStop,
}) => {

  const [show,setShow] = useState(true)

  useEffect(() => {
    client.query({ query }).then(response => {
      let edges = response.data.stopsByRadius.edges
      let stopit = edges
        .map(edge => edge.node.stop)
        .filter(stop => stop.vehicleType === 0)
      setStops(stopit)
      setMyStop(stopit[0])
      console.log('EDGES: ', edges)
      console.log('STOPIT: ', stopit)
      edges.forEach(edge => {
        console.log('HSL: ', edge.node.stop)
      })
    })
  })

  const update = () => {
    //fetch('http://localhost:3001/trams')
    fetch('/trams')
      .then(response => response.json())
      .then(body => {
        setTrams(body)
        setShowTrams(body)
      })
      .catch(error => {
        console.log(error)
      })
  }

  //useEffect(() => update(), [new Date()])
  setInterval(() => {
    update()
  }, 1000)

  const openSidebar = () => {
    setShowSidebar(true)
    setShowSidebarOpenButton(false)
    console.log('open!')
  }

  const closeSidebar = () => {
    setShowSidebar(false)
    setTimeout(() => {
      setShowSidebarOpenButton(true)
    }, 300)
    console.log('close!')
  }

  return (
    <div>
      <Alert id='alert' show={show} variant='danger'>
        <br/><br/><br/><br/><br/>      
        <Alert.Heading>How's it going?!</Alert.Heading>
        <p>
          Duis mollis, est non commodo luctus, nisi erat porttitor ligula, eget
          lacinia odio sem nec elit. Cras mattis consectetur purus sit amet
          fermentum.
        </p>
        <hr />
        <div className='d-flex justify-content-end'>
          <Button onClick={() => setShow(false)} variant='outline-success'>
            Close me ya'll!
          </Button>
        </div>
      </Alert>
      <Sidebar closeSidebar={closeSidebar} />
      <LeafletMap openSidebar={openSidebar} closeSidebar={closeSidebar} />
    </div>
  )
}

/* const mapStateToProps = state => {
  return {
    stops: state.stops,
    myStop: state.myStop,
  }
} */

const mapDispatchToProps = {
  setTrams,
  setShowSidebar,
  setShowSidebarOpenButton,
  setStops,
  setMyStop,
  setShowTrams,
}

export default connect(
  null,
  mapDispatchToProps
)(App)

