const tramsReducer = (state = {trams:[],myTram:{}}, action) => {
  switch (action.type) {
    case 'SET_TRAMS':
      return {state, trams: action.data}
    case 'SET_MY_TRAM':
      return {tram, myTram: action.data}
    default:
      return state
  }
}

export const setTrams = trams => {
  return dispatch => {
    dispatch({
      type: 'SET_TRAMS',
      data: trams,
    })
  }
}

export const setMyTram = tram => {
  return dispatch => {
    dispatch({
      type: 'SET_MY_TRAM',
      data: tram,
    })
  }
}

export default tramsReducer
